import  {FaTimes} from 'react-icons/fa'

const Task = ({task , onDelete , onToggle }) => { 
        return (
            <div className={`task ${task.reminder ? 'reminder' : ''} `}   onDoubleClick={()=>onToggle(task.id)}>
                <h3>{task.text}</h3>
                <FaTimes 
                style={{color:'red',marginLeft:'20em'}} 
                onClick={()=> onDelete(task.id)}
               
                />
                <p>{task.day}</p>
            </div>
        )
    }

export default Task
