import  {useState} from 'react' ;

const tasks = [
    {
        id:1,
        text : 'Doctors Appointment',
        day:'Feb 6th at 1:30pm' ,
        reminder : true ,
    },
    {
        id:2,
        text:'meeting at school' ,
        day:'Feb 19th at 18:30pm' ,
        reminder : true ,
    },
    {
        id:3,
        text:'Food Shopping ' ,
        day:'Feb 3th at 1:30pm' ,
        reminder : false ,
    }
]
const Tasks = () => { 
        return (
            <>
            {tasks.map((task)=>(
                // <h3>{task.text}</h3>

                <h4 key={task.id}>{task.day}</h4>
            ))} 
            </>
        )
    }


export default Tasks
