  task.id === id ? {... task , reminder : !task.reminder} : task 

  ))
}


  return (
    <div className="container">
   <Header 
       onAdd={()=> setShowAddTask(!showAddTask) }
       showAdd={showAddTask}
    />
  { showAddTask && <AddTask onAdd={addTask} />}
    {
            tasks.length > 0 ?
              (<Tasks 
                tasks={tasks} 
                onDelete={deleteTask}
                onToggle={toggleReminder}
                />)
              : 'No tasks To show !'
     }
    </div>
  );
}

export default App;
